$ctrl.loaded = false
$ctrl.views.project.tasks = []

if (!$ctrl.data) return

const result = await $ctrl.demoTaskApi.demo_api_tasks_services_task_SelectAllTaskByProject({
    projectCode: $ctrl.data.code,
    limit: 1000,
    offset: 0
})

$ctrl.$timeout(() => {
    if (!$ctrl.data) return
    const tasks = result.data.task
    const users = ($ctrl.data.assignedUsers ?? [])

    tasks.forEach(task => {
        task.user = users.find(user => user.userId === task.assignedUserId)
    })

    $ctrl.loaded = true
    $ctrl.views.project.tasks = tasks
    $ctrl.projectManager.refreshData($ctrl.views.project)
})

if (!$ctrl.data) return ''
const deadLine = $ctrl.data.deadLine
const date = moment(deadLine)
return date.format('MMM DD, YYYY')

const id = $ctrl.data.projectId
const index = $ctrl.views.task.notes.findIndex(data => data.taskId === id)

const actions = [{
    name: 'yes', label: 'Yes', type: 'danger', action: async () => {
        await $ctrl.demoTaskApi.demo_api_tasks_services_project_DeleteProject({ projectId: id })
        await $ctrl.projectManager.refreshProjects()

        if ($ctrl.search.filter) $ctrl.search.execute()

        $ctrl.hide()
    }
}, { name: 'no', label: 'No' }]

$ctrl.$modal.open('action-modal', { hideCancel: true, actions, message: "Are you sure you want to delete this project?" })
